import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.nifi.processor.ProcessSession
import java.nio.file.Paths

// Функция для рекурсивного обхода папок
def traverseFolders(File folder, String relativePath, List<String> folderList) {
    // Получаем список файлов и подпапок в текущей папке
    def files = folder.listFiles()

    // Если папка пуста, выходим
    if (files == null || files.length == 0) {
        return
    }

    // Обходим все элементы в папке
    files.each { file ->
        if (file.isDirectory()) {
            // Добавляем путь к папке в список
            def fullFolderPath = Paths.get(relativePath, file.name).toString()
            folderList << fullFolderPath

            // Рекурсивно обходим подпапку
            traverseFolders(file, fullFolderPath, folderList)
        }
    }
}

// Глобальные переменные
def folderPath = absolute_path.value

// Проверяем, задан ли путь к папке
if (!folderPath) {
    throw new IllegalStateException("Не задан путь в свойстве absolute_path")
}

// Проверяем, существует ли папка и является ли она директорией
def folder = new File(folderPath)
if (!folder.exists() || !folder.isDirectory()) {
    throw new IllegalStateException("Указанная папка не существует или не является директорией: ${folderPath}")
}

// Основной код
try {
    // Список для хранения путей к папкам
    def folderList = []

    // Начинаем рекурсивный обход с корневой папки
    traverseFolders(folder, "", folderList)

    // Создаем новый FlowFile
    def flowFile = session.create()

    // Устанавливаем атрибут mime.type
    flowFile = session.putAttribute(flowFile, "mime.type", "text/plain")

    // Устанавливаем атрибут с полным путём к папке
    flowFile = session.putAttribute(flowFile, "absolute.path", folderPath)

    // Записываем список папок в содержимое FlowFile
    flowFile = session.write(flowFile, { outputStream ->
        folderList.each { currentFolderPath -> // Используем другое имя переменной
            outputStream.write((currentFolderPath + "\n").getBytes("UTF-8"))
        }
    } as OutputStreamCallback)

    // Передаем FlowFile в выходное отношение
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Ошибка при выполнении скрипта: ${e.message}", e)
    session.rollback()
    return
}

import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.nifi.processor.ProcessSession
import java.nio.file.Paths

// Функция для рекурсивного обхода папки
def traverseFolder(File folder, String relativePath, ProcessSession session, def excludePattern, def includePattern, def baseFolderPath) {
    // Получаем список файлов и подпапок в текущей папке
    def files = folder.listFiles()

    // Если папка пуста, выходим
    if (files == null || files.length == 0) {
        return
    }

    // Собираем список файлов в текущей папке
    def fileList = []
    files.each { file ->
        // Проверяем, не исключается ли файл или папка по регулярному выражению
        if (excludePattern && file.name =~ excludePattern) {
            log.debug("Исключен путь: ${file.name}")
            return // Пропускаем этот файл или папку
        }

        if (file.isFile()) {
            // Проверяем, соответствует ли файл регулярному выражению для включения
            if (includePattern && !(file.name =~ includePattern)) {
                log.debug("Файл не соответствует include_regex: ${file.name}")
                return // Пропускаем этот файл
            }

            // Добавляем только имя файла (без пути)
            fileList << file.name
        } else if (file.isDirectory()) {
            // Рекурсивно обходим подпапку
            traverseFolder(file, relativePath + "/" + file.name, session, excludePattern, includePattern, baseFolderPath)
        }
    }

    // Если в папке есть файлы, создаем FlowFile
    if (fileList.size() > 0) {
        // Создаем новый FlowFile
        def flowFile = session.create()

        // Формируем полный путь к папке с использованием Paths
        def fullFolderPath = Paths.get(baseFolderPath, relativePath).toString()

        // Устанавливаем атрибут с полным путём к папке
        flowFile = session.putAttribute(flowFile, "absolute.path", fullFolderPath)

        // Устанавливаем атрибут mime.type
        flowFile = session.putAttribute(flowFile, "mime.type", "text/plain")

        // Записываем список файлов в содержимое FlowFile
        flowFile = session.write(flowFile, { outputStream ->
            fileList.each { fileName ->
                outputStream.write((fileName + "\n").getBytes("UTF-8"))
            }
        } as OutputStreamCallback)

        // Передаем FlowFile в выходное отношение
        session.transfer(flowFile, REL_SUCCESS)
    }
}

// Глобальные переменные
def folderPath = absolute_path.value
excludeRegex = exclude_folder_regex.value ?: null
def excludePattern = excludeRegex ? ~excludeRegex : null
includeRegex = include_file_regex.value ?: null
def includePattern = includeRegex ? ~/(?i)${includeRegex}/ : null // Добавляем (?i) для case-insensitive

// Проверяем, задан ли путь к папке
if (!folderPath) {
    throw new IllegalStateException("Не задан путь в свойстве absolute_path")
}

// Проверяем, существует ли папка и является ли она директорией
def folder = new File(folderPath)
if (!folder.exists() || !folder.isDirectory()) {
    throw new IllegalStateException("Указанная папка не существует или не является директорией: ${folderPath}")
}

// Основной код
try {
    // Начинаем рекурсивный обход с корневой папки
    traverseFolder(folder, "", session, excludePattern, includePattern, folderPath)

} catch (Exception e) {
    log.error("Ошибка при выполнении скрипта: ${e.message}", e)
    session.rollback()
    return
}
