import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import org.apache.nifi.processor.io.StreamCallback

def flowFile = session.get()
if (!flowFile) return

String myAttr = myAttr.value

flowFile = session.write(flowFile, { inputStream, outputStream ->
    // Читаем весь входной поток как JSON-массив
    def jsonSlurper = new JsonSlurper()
    def jsonArray = jsonSlurper.parse(inputStream)

    // Обрабатываем каждый объект в массиве
    def processedArray = jsonArray.collect { parsedJson ->
        if (["pause", "halt"].contains(parsedJson.attr1)) {
            parsedJson.attr2 = myAttr
        }
        parsedJson
    }

    // Записываем обработанный массив в выходной поток
    outputStream.withWriter("UTF-8") { writer ->
        writer.write(JsonOutput.toJson(processedArray))
    }
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
