import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.nifi.processor.ProcessSession
import java.nio.file.Paths
import java.nio.file.Files
import java.nio.file.Path
import java.util.regex.Pattern

// Глобальные переменные
def folderPath = absolute_path.value
def excludeFolderRegex = exclude_folder_regex.value // Динамический атрибут для исключения папок

// Проверяем, задан ли путь к папке
if (!folderPath) {
    throw new IllegalStateException("Не задан путь в свойстве absolute_path")
}

// Проверяем, существует ли папка и является ли она директорией
def folder = new File(folderPath)
if (!folder.exists() || !folder.isDirectory()) {
    throw new IllegalStateException("Указанная папка не существует или не является директорией: ${folderPath}")
}

// Компилируем регулярное выражение для исключения папок
Pattern excludePattern = null
if (excludeFolderRegex) {
    excludePattern = Pattern.compile(excludeFolderRegex)
}

// Функция для проверки, содержит ли папка файлы (нерекурсивно)
def containsFilesNonRecursive(Path path) {
    try {
        return Files.list(path)
            .anyMatch { p -> Files.isRegularFile(p) }
    } catch (Exception e) {
        log.error("Ошибка при проверке папки ${path}: ${e.message}", e)
        return false
    }
}

// Основной код
try {
    // Обрабатываем корневую папку
    if (containsFilesNonRecursive(Paths.get(folderPath))) {
        def flowFile = session.create()

        // Устанавливаем атрибуты
        flowFile = session.putAttribute(flowFile, "mime.type", "text/plain")
        flowFile = session.putAttribute(flowFile, "absolute.path", folderPath)

        // Записываем путь в содержимое FlowFile
        flowFile = session.write(flowFile, { outputStream ->
            outputStream.write("${folderPath}\n".getBytes("UTF-8"))
        } as OutputStreamCallback)

        // Передаем FlowFile в выходное отношение
        session.transfer(flowFile, REL_SUCCESS)
    }

    // Обрабатываем вложенные папки
    Files.walk(Paths.get(folderPath))
        .filter { path ->
            // Исключаем корневую папку
            if (path == Paths.get(folderPath)) {
                return false
            }

            // Проверяем, является ли путь папкой
            if (!Files.isDirectory(path)) {
                return false
            }

            // Проверяем, исключена ли папка по регулярному выражению
            if (excludePattern != null && excludePattern.matcher(path.getFileName().toString()).matches()) {
                return false
            }

            // Проверяем, содержит ли папка файлы (нерекурсивно)
            containsFilesNonRecursive(path)
        }
        .forEach { path ->
            def flowFile = session.create()

            // Устанавливаем атрибуты
            flowFile = session.putAttribute(flowFile, "mime.type", "text/plain")
            flowFile = session.putAttribute(flowFile, "absolute.path", path.toString())

            // Записываем путь в содержимое FlowFile
            flowFile = session.write(flowFile, { outputStream ->
                outputStream.write("${path}\n".getBytes("UTF-8"))
            } as OutputStreamCallback)

            // Передаем FlowFile в выходное отношение
            session.transfer(flowFile, REL_SUCCESS)
        }

} catch (Exception e) {
    log.error("Ошибка при выполнении скрипта: ${e.message}", e)
    session.rollback()
    return
}

import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.ProcessSession
import java.nio.file.Paths
import java.nio.file.Files
import java.nio.file.Path
import java.util.regex.Pattern
import java.util.regex.PatternSyntaxException

// Основной код
try {
    // Читаем входящий FlowFile
    def flowFile = session.get()
    if (flowFile == null) {
        return
    }

    // Читаем содержимое FlowFile (путь к папке)
    def folderPath = ""
    session.read(flowFile, { inputStream ->
        folderPath = new BufferedReader(new InputStreamReader(inputStream)).readLine()
    } as InputStreamCallback)

    // Проверяем, задан ли путь к папке
    if (!folderPath) {
        log.error("Путь к папке не найден во входящем FlowFile")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Читаем динамическое свойство процессора "File Filter"
    def fileFilter = context.getProperty("File Filter").getValue()

    // Проверяем, валидно ли регулярное выражение
    def pattern
    try {
        pattern = fileFilter ? Pattern.compile(fileFilter) : Pattern.compile(".*")
    } catch (PatternSyntaxException e) {
        log.error("Некорректное регулярное выражение в свойстве 'File Filter': ${fileFilter}. Используется значение по умолчанию '.*'")
        pattern = Pattern.compile(".*")
    }

    // Получаем список файлов в папке (нерекурсивно)
    def files = Files.list(Paths.get(folderPath))
        .filter { path -> 
            Files.isRegularFile(path) && pattern.matcher(path.fileName.toString()).matches()
        }
        .toList()

    // Создаем FlowFile для каждого файла
    files.each { filePath ->
        def newFlowFile = session.create()

        // Устанавливаем атрибуты
        newFlowFile = session.putAttribute(newFlowFile, "absolute.path", filePath.parent.toString())
        newFlowFile = session.putAttribute(newFlowFile, "filename", filePath.fileName.toString())

        // Передаем FlowFile в выходное отношение
        session.transfer(newFlowFile, REL_SUCCESS)
    }

    // Удаляем оригинальный FlowFile
    session.remove(flowFile)

} catch (Exception e) {
    log.error("Ошибка при выполнении скрипта: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
    return
}
